mapper.select:
SELECT

FROM ACCOUNT_CO_INVESTMENT_ACCOUNT a WHERE 1 = 1

    AND a.ACCOUNT_ID = #{accountId}


    AND a.IS_STOCK_INCOME_GROWTH_PLAN = #{isStockIncomeGrowthPlan}


    AND a.ACCOUNT_NAME LIKE '%'||#{accountName}||'%'


    AND a.SIGNATURE_DATE = #{signatureDate}


    AND a.SIGNATURE = #{signature}


    AND a.CREATED_BY = #{createdBy}


    AND a.CREATED_DATE = #{createdDate}


    AND a.UPDATED_BY = #{updatedBy}


    AND a.UPDATED_DATE = #{updatedDate}
