mapper.select:
SELECT

FROM ACCOUNT_CO_WEALTH_SOURCE a WHERE 1=1

    AND a.ACCOUNT_ID LIKE '%'||#{accountId}||'%'


    AND a.INCOME_TYPE LIKE '%'||#{incomeType}||'%'


    AND a.IS_CAPITAL_INJECTION = #{isCapitalInjection}


    AND a.PERCENTAGE_OF_FUNDS = #{percentageOfFunds}


    AND a.SOURCE_DESC LIKE '%'||#{sourceDesc}||'%'


    AND a.CREATED_BY = #{createdBy}


    AND a.CREATED_DATE = #{createdDate}


    AND a.UPDATED_BY = #{updatedBy}


    AND a.UPDATED_DATE = #{updatedDate}
