mapper.select:
SELECT

FROM ACCOUNT_PAR_BROKER_INFO a WHERE 1 = 1 

    AND a.ACCOUNT_ID = #{accountId}


    AND a.STATUS = #{status}


    AND a.OCC_MEMBER_NUMBER LIKE '%'||#{occMemberNumber}||'%'


    AND a.REGULATOR LIKE '%'||#{regulator}||'%'


    AND a.REGULATOR_INFO LIKE '%'||#{regulatorInfo}||'%'


    AND a.REGISTER_NUMBER LIKE '%'||#{registerNumber}||'%'


    AND a.REGISTER_POSITION LIKE '%'||#{registerPosition}||'%'


    AND a.CREATED_BY = #{createdBy}


    AND a.CREATED_DATE = #{createdDate}


    AND a.UPDATED_BY = #{updatedBy}


    AND a.UPDATED_DATE = #{updatedDate}
