mapper.select:
SELECT

FROM ACCOUNT_PAR_BROKER_PERMISSION a WHERE 1 = 1 

    AND a.ACCOUNT_ID = #{accountId}


    AND a.REGISTER_ADVISER LIKE '%'||#{registerAdviser}||'%'


    AND a.REGISTER_COUNTRY LIKE '%'||#{registerCountry}||'%'


    AND a.CREATED_BY = #{createdBy}


    AND a.CREATED_DATE = #{createdDate}


    AND a.UPDATED_BY = #{updatedBy}


    AND a.UPDATED_DATE = #{updatedDate}
