mapper.select:
SELECT

FROM ACCOUNT_PER_FILE a WHERE 1 = 1

    AND a.ACCOUNT_ID = #{accountId}


    AND a.FILE_PATH LIKE '%'||#{filePath}||'%'


    AND a.FILE_NAME LIKE '%'||#{fileName}||'%'


    AND a.FILE_IDENTIFICATION = #{fileIdentification}


    AND a.CREATED_BY = #{createdBy}


    AND a.CREATED_DATE = #{createdDate}


    AND a.UPDATED_BY = #{updatedBy}


    AND a.UPDATED_DATE = #{updatedDate}
