mapper.select:
SELECT

FROM ACCOUNT_PER_PORTFOLIO a WHERE 1 = 1

    AND a.ACCOUNT_ID = #{accountId}


    AND a.ACCOUNT_PORTFOLIO_TYPE LIKE '%'||#{accountPortfolioType}||'%'


    AND a.BASE_CURRENCY LIKE '%'||#{baseCurrency}||'%'


    AND a.CREATED_BY = #{createdBy}


    AND a.CREATED_DATE = #{createdDate}


    AND a.UPDATED_BY = #{updatedBy}


    AND a.UPDATED_DATE = #{updatedDate}
