mapper.select:
SELECT

FROM ACCOUNT_PER_REGULATORY a WHERE 1 = 1

    AND a.ACCOUNT_ID = #{accountId}


    AND a.EMPLOYER LIKE '%'||#{employer}||'%'


    AND a.RELATIONSHIP_ACCOUNT_HOLDERS LIKE '%'||#{relationshipAccountHolders}||'%'


    AND a.FAMILY_MEMBERS_COMPANY LIKE '%'||#{familyMembersCompany}||'%'


    AND a.ACCOUNT_HOLDER_COMPANY_STOCK LIKE '%'||#{accountHolderCompanyStock}||'%'


    AND a.CREATED_BY = #{createdBy}


    AND a.CREATED_DATE = #{createdDate}


    AND a.UPDATED_BY = #{updatedBy}


    AND a.UPDATED_DATE = #{updatedDate}
