mapper.select:
SELECT

FROM EXPRESS a
LEFT JOIN USER_INFO b ON a.UPDATE_BY = b.ID
WHERE 1 = 1

    AND a.TITLE LIKE '%'||#{title}||'%'


    AND a.TYPE = #{type}


    AND a.SOURCE LIKE '%'||#{source}||'%'


    AND a.SUMMARY LIKE '%'||#{summary}||'%'


    AND a.PICT_URL LIKE '%'||#{pictUrl}||'%'


    AND a.CONTENT LIKE '%'||#{content}||'%'


    AND a.LANG  = #{lang}


    AND a.STATUS = #{status}


    AND a.IS_TOP = #{isTop}


    AND a.READING_QUANTITY = #{readingQuantity}


    AND a.THUMBS_UP = #{thumbsUp}


    AND a.CREATE_BY = #{createBy}


    AND a.CREATE_DATE = #{createDate}


    AND a.UPDATE_BY = #{updateBy}


    AND a.UPDATE_DATE = #{updateDate}


    AND a.CREATE_DATE >= #{createDateStart}
    AND a.CREATE_DATE <= #{createDateEnd}

ORDER BY a.IS_TOP desc,a.UPDATE_DATE desc

SELECT

FROM EXPRESS a, USER_INFO b
WHERE a.CREATE_BY = b.ID AND a.ID = #{id}
