mapper.select:
SELECT

FROM HELP_CENTER a
LEFT JOIN USER_INFO b ON a.UPDATE_BY = b.ID
WHERE 1 = 1 

    AND a.PROBLEM_LEVEL_ONE LIKE '%'||#{problemLevelOne}||'%'


    AND a.PROBLEM_LEVEL_TWO LIKE '%'||#{problemLevelTwo}||'%'


    AND a.PROBLEM_TYPE LIKE '%'||#{problemType}||'%'


    AND a.PROBLEM_SOLVING LIKE '%'||#{problemSolving}||'%'


    AND a.SEARCH_CONTENT LIKE '%'||#{searchContent}||'%'


    AND a.PICT_URL LIKE '%'||#{pictUrl}||'%'


    AND a.STATUS = #{status}


    AND a.LANG LIKE '%'||#{lang}||'%'


    AND a.IS_HOT = #{isHot}


    AND a.CREATE_BY = #{createBy}


    AND a.CREATE_DATE = #{createDate}


    AND a.UPDATE_BY = #{updateBy}


    AND a.UPDATE_DATE = #{updateDate}


    AND a.CREATE_DATE >= #{createDateStart}
    AND a.CREATE_DATE <= #{createDateEnd}

ORDER BY a.IS_HOT desc, a.UPDATE_DATE desc

SELECT

FROM HELP_CENTER a
WHERE a.PROBLEM_TYPE = #{problemType}
AND a.PROBLEM_LEVEL_ONE = #{problemLevelOne}
AND a.LANG = #{lang}
AND a.STATUS = 1
ORDER BY a.IS_HOT desc, a.UPDATE_DATE desc
