mapper.select:
SELECT

FROM NEWS_BULLETIN a WHERE 1 = 1 

    AND a.TITLE LIKE '%'||#{title}||'%'


    AND a.TYPE LIKE '%'||#{type}||'%'


    AND a.CONTENT LIKE '%'||#{content}||'%'


    AND a.LANG LIKE '%'||#{lang}||'%'


    AND a.STATUS = #{status}


    AND a.IS_TIMED_JOB = #{isTimedJob}


    AND a.CREATE_BY = #{createBy}


    AND a.CREATE_DATE = #{createDate}


    AND a.UPDATE_BY = #{updateBy}


    AND a.UPDATE_DATE = #{updateDate}


    AND a.CRON LIKE '%'||#{cron}||'%'


    AND a.CLIENT_TYPE LIKE '%'||#{clientType}||'%'


    AND a.RELEASE_DATE = #{releaseDate}

SELECT

FROM NEWS_BULLETIN a WHERE 1 = 1 

    AND a.TYPE = #{model.type}


    AND a.LANG = #{model.lang}


    AND a.STATUS = #{model.status}


    AND a.CLIENT_TYPE LIKE '%'||#{model.clientType}||'%'


    AND a.CREATE_DATE >= #{model.startDate}
    AND a.CREATE_DATE <= #{model.endDate}

ORDER BY a.UPDATE_DATE DESC NULLS LAST

SELECT

FROM NEWS_BULLETIN a WHERE 1 = 1 

    AND a.TYPE = #{model.type}


    AND a.LANG = #{model.lang}


    AND a.STATUS = #{model.status}


    AND a.CLIENT_TYPE LIKE '%'||#{model.clientType}||'%'


    AND a.CREATE_DATE >= #{model.startDate}
    AND a.CREATE_DATE <= #{model.endDate}

ORDER BY a.RELEASE_DATE DESC NULLS LAST
