mapper.select:
SELECT , b.ACCOUNT_NAME as accountName
FROM IB_ACCOUNT_MARKET_VALUE a
LEFT JOIN ACCOUNT b ON a.ACCOUNT_ID = b.ID
WHERE 1 = 1

    AND a.ACCOUNT_ID = #{accountId}


    AND a.ACCOUNT_CODE LIKE '%'||#{accountCode}||'%'


    AND a.CURRENCY LIKE '%'||#{currency}||'%'


    AND a.TOTAL_CASH_BALANCE = #{totalCashBalance}


    AND a.CASH_BALANCE = #{cashBalance}


    AND a.ACCRUED_CASH = #{accruedCash}


    AND a.STOCK_MARKET_VALUE = #{stockMarketValue}


    AND a.OPTION_MARKET_VALUE = #{optionMarketValue}


    AND a.FUTURE_OPTION_VALUE = #{futureOptionValue}


    AND a.FUTURES_PNL = #{futuresPnl}


    AND a.NET_LIQUIDATION_BY_CURRENCY = #{netLiquidationByCurrency}


    AND a.FUND_VALUE = #{fundValue}


    AND a.NET_DIVIDEND = #{netDividend}


    AND a.MUTUAL_FUND_VALUE = #{mutualFundValue}


    AND a.MONEY_MARKET_FUND_VALUE = #{moneyMarketFundValue}


    AND a.CORPORATE_BOND_VALUE = #{corporateBondValue}


    AND a.T_BOND_VALUE = #{tBondValue}


    AND a.T_BILL_VALUE = #{tBillValue}


    AND a.WARRANT_VALUE = #{warrantValue}


    AND a.FX_CASH_BALANCE = #{fxCashBalance}


    AND a.ACCOUNT_OR_GROUP LIKE '%'||#{accountOrGroup}||'%'


    AND a.REAL_CURRENCY LIKE '%'||#{realCurrency}||'%'


    AND a.ISSUER_OPTION_VALUE = #{issuerOptionValue}


    AND a.UNREALIZED_PNL = #{unrealizedPnl}


    AND a.REALIZED_PNL = #{realizedPnl}


    AND a.EXCHANGE_RATE = #{exchangeRate}


    AND a.REMARK LIKE '%'||#{remark}||'%'


    AND a.CREATE_DATE = #{createDate}


    AND a.UPDATE_DATE = #{updateDate}
