mapper.select:
SELECT , b.ACCOUNT_NAME as accountName
FROM IB_ACCOUNT_TRADE a
LEFT JOIN ACCOUNT b ON a.ACCOUNT_ID = b.ID
WHERE 1 = 1

    AND a.ACCOUNT_ID = #{accountId}


    AND a.ACCOUNT_CODE LIKE '%'||#{accountCode}||'%'


    AND a.SYMBOL LIKE '%'||#{symbol}||'%'


    AND a.SEC_TYPE LIKE '%'||#{secType}||'%'


    AND a.CURRENCY LIKE '%'||#{currency}||'%'


    AND a.EXCHANGE LIKE '%'||#{exchange}||'%'


    AND a.EXEC_ID LIKE '%'||#{execId}||'%'


    AND a.ORDER_ID LIKE '%'||#{orderId}||'%'


    AND a.SIDE LIKE '%'||#{side}||'%'


    AND a.PRICE = #{price}


    AND a.SHARES = #{shares}


    AND a.AVG_PRICE = #{avgPrice}


    AND a.EXEC_TIME LIKE '%'||#{execTime}||'%'


    AND a.REMARK LIKE '%'||#{remark}||'%'


    AND a.CREATE_DATE = #{createDate}


    AND a.UPDATE_DATE = #{updateDate}


    AND TO_NUMBER(
    TO_CHAR(TO_DATE(a.EXEC_TIME, 'YYYY-MM-DD HH24:MI:SS'), 'YYYYMMDDHH24MISS')
    ) >= TO_NUMBER(
    TO_CHAR(#{execTimeStart}/ (1000 * 60 * 60 * 24) + TO_DATE('1970-01-01 08:00:00', 'YYYY/MM/DD HH:MI:SS'), 'YYYYMMDDHH24MISS')
    )


    AND TO_NUMBER(
    TO_CHAR(TO_DATE(a.EXEC_TIME, 'YYYY-MM-DD HH24:MI:SS'), 'YYYYMMDDHH24MISS')
    ) <= TO_NUMBER(
    TO_CHAR(#{execTimeEnd}/ (1000 * 60 * 60 * 24) + TO_DATE('1970-01-01 08:00:00', 'YYYY/MM/DD HH:MI:SS'), 'YYYYMMDDHH24MISS')
    )

order by a.ACCOUNT_ID,a.Account_Code,a.Currency,a.CREATE_DATE
