mapper.select:
SELECT

FROM OAUTH_ACCESS_TOKEN a WHERE 1 = 1 

    AND a.TOKEN_ID = #{tokenId}


    AND a.AUTHENTICATION_ID = #{authenticationId}


    AND a.USER_NAME = #{userName}


    AND a.CLIENT_ID = #{clientId}


    AND a.TOKEN_TYPE = #{tokenType}


    AND a.REFRESH_TOKEN = #{refreshToken}

SELECT

FROM OAUTH_ACCESS_TOKEN a
WHERE a.REFRESH_TOKEN = #{refreshToken}
AND a.CLIENT_ID = #{clientId}

SELECT

FROM OAUTH_ACCESS_TOKEN a
WHERE a.USER_NAME = #{userName}
AND a.CLIENT_ID = #{clientId}
AND a.AUTHENTICATION_ID = #{authenticationId}

SELECT

FROM OAUTH_ACCESS_TOKEN a
WHERE a.TOKEN_ID = #{tokenId}
